import React from "react";

// https://rickandmortyapi.com/api/character/avatar/20.jpeg

class Todo extends React.Component {
  // shouldComponentUpdate(nextProps, nextState) {
  //   return nextProps.id !== this.props.id;
  // }
  render() {
    // const { text, users, isDone } = this.props.data;
    const {
      removeUserFromTodo,
      data: { text, users, isDone }
    } = this.props;
    return (
      <li className="media" style={{ borderBottom: "1px solid #ccc" }}>
        <div className="media-left" />
        <div className="media-body">
          <h4 className="media-heading">{text}</h4>
          <label>
            {" "}
            <input checked={isDone} type="checkbox" /> done{" "}
          </label>{" "}
          <ul className="list-unstyled list-inline" style={{ float: "right" }}>
            {users.map(user => (
              <li>
                <a onClick={removeUserFromTodo}>
                  <img
                    className="img-circle"
                    title={`By ${user.name}`}
                    style={{
                      width: "50px",
                      height: "50px",
                      float: "right"
                    }}
                    src={user.imageUrl}
                  />
                  <br />
                  Remove
                </a>
              </li>
            ))}
          </ul>
        </div>
      </li>
    );
  }
}

export default Todo;
