import React from "react";
import Todo from "./Todo";
import { convertNumberToEnglish, createRange } from "../utils";

const NUM_ITEMS = 100;

let guid = 0;

// const items = [];
// for (let i = 0; i < NUM_ITEMS; i++)
//   items.push({ id: ++guid, text: `item ${guid}` });

class TodoList extends React.Component {
  constructor() {
    super();
    this.state = {
      inputValue: "",
      items: createRange(NUM_ITEMS).map(n => ({
        key: ++guid,
        text: convertNumberToEnglish(n + 1),
        isDone: Math.random() < 0.5
      }))
    };
  }

  handleInputChange = e => {
    this.setState({
      inputValue: e.target.value
    });
  };

  handleSubmit = e => {
    e.preventDefault();

    const item = {
      id: ++guid,
      text: e.target.elements[0].value
    };

    this.setState(state => ({
      items: [item, ...state.items]
    }));
  };

  render() {
    return (
      <div style={{ width: "600px", margin: "0 auto" }}>
        <form
          style={{ paddingTop: "20px", paddingBottom: "10px" }}
          onSubmit={this.handleSubmit}
        >
          <h2>Todo 1 ({NUM_ITEMS} items)</h2>
          <h3>New task</h3>
          <input
            value={this.state.inputValue}
            onChange={this.handleInputChange}
            className="form-control"
          />
        </form>
        <ul className="list-unstyled">
          {this.state.items.map(item => (
            <Todo key={item.id} text={item.text} />
          ))}
        </ul>
      </div>
    );
  }
}

export default TodoList;
